  const viewRender = view => {
    switch(view){
      case "home":{
        return(
          <Home setView={setView}/>
        )
      }
      case "grade":{
        return(
          <div style={{gridRowStart:2}}><GradePage setView={setView}/></div>
        )
      }
      case "scholar":{
        return(
          <div style={{gridRowStart:2}}><ScholarPage setView={setView}/></div>
        )
      }
      // case "tutors":{
