      }
    }
  }

  if(isLoading){
    return(
      <div style={{width:"100%",height:"100vh",display:'flex',justifyContent:'center',alignItems:'center'}}>
      {/* <div style={{width:"100%",height:"100vh",backgroundImage:"linear-gradient(to right, rgba(16, 231, 220,1) , rgba(27, 156, 229,1))",display:'flex',justifyContent:'center',alignItems:'center'}}> */}
